import React, { useState } from 'react'
import { StyleSheet, SafeAreaView, View, TextInput, Text } from 'react-native'
import { useLocation, useNavigate } from 'react-router-dom'


const AddNote = ({ handleAddNote }) => {
	const navigate = useNavigate();
	const path = useLocation();

	const [noteText, setNoteText] = useState('');

	const [noteKey, setNoteKey] = useState('');
	const characterLimit = 200;

	const handleChange = (event) => {
		if (characterLimit - event.target.value.length >= 0) {
			setNoteKey(event.target.value)
		}
	};

	const handleChange2 = (event) => {
		if (characterLimit - event.target.value.length >= 0) {
			setNoteText(event.target.value)
		}
	};

	const handleSaveClick = () => {
		if (noteText.trim().length > 0 && noteKey.trim().length > 0) {
			handleAddNote(noteText, noteKey)
			setNoteKey('')
			setNoteText('')

			function f1() {
			navigate('/')
			}
			setTimeout(f1, 200)
		}
	}

  	return (
    <SafeAreaView style={styles.container}>
			<View style={styles.noteNew}>
			<TextInput style={styles.key}
					multiline={false}
					numberOfLines={0}
					placeholder='Введіть рік'
				value={noteKey}
				onChange={handleChange}
			></TextInput>
			<Text style={styles.line}>_________</Text>
			<TextInput style={styles.input}
					multiline={false}
					numberOfLines={0}
					placeholder='Далі наберіть текст...'
				value={noteText}
				onChange={handleChange2}
			></TextInput>
			<View style={styles.noteFooter}>
				<View style={{ flexDirection: 'row', marginLeft: '65%' }}>
					<View style={styles.cancel} onClick={() => navigate('/')}>
						<Text style={styles.textBtn}>скасувати</Text>
					</View>
					<View style={styles.btn} onClick={handleSaveClick}>
						<Text style={styles.textBtn}>зберегти</Text>
					</View>
				</View>
			</View>
			</View>
		</SafeAreaView>
  	)
}

const styles = StyleSheet.create({
	noteNew: {
		color: '#fff',
		borderBottomColor: '#fff',
		backgroundColor: '#fff',
		width: '94%',
		marginLeft: '3%',
		marginTop: '3%',

	},
	container: {
		border: 'dashed',
		color: '#d8bff5',
		width: '80%',
		marginLeft: '10%',
		backgroundColor: '#fff',
		borderStartColor: '#d8bff5',
		borderEndColor: '#d8bff5',
		borderTopColor: '#d8bff5',
		borderBottomColor: '#d8bff5',
		borderRadius: 25,
		padding: '1rem',
		height: 290,
		marginTop: 20,
	},
	noteFooter: {
		color: '#666',
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
		fontSize: 21,
	},
	input: {
		fontSize: 17,
		height: 40,
		marginTop: 40,
		color: 'black',
	},
	key: {
		fontSize: 17,
		height: 45,
		color: '#9e5cf6',
	},
	btn: {
		alignItems: 'center',
		textAlign: 'center',
		border: 'none',
		backgroundColor: '#9e5cf6',
		borderRadius: 25,
		height: 45,
		width: 185,
		color: 'white',
		cursor: 'pointer',
	},
	cancel: {
		alignItems: 'center',
		marginRight: '4%',
		border: 'none',
		backgroundColor: '#9e5cf6',
		borderRadius: 25,
		height: 45,
		width: 185,
		color: 'white',
		cursor: 'pointer',
	},
	textBtn: {
		marginTop: 5,
		fontSize: 22,
		color: 'white',
		fontWeight: 600,
	},
	line: {
		letterSpacing: -1,
		marginTop: -35,
		marginBottom: 15,
		fontSize: 30,
		height: 2,
		color: '#9e5cf6',
	},
});

export default AddNote
