import React from "react"
import { json, useLocation, useNavigate } from "react-router-dom"
import { StyleSheet, View, Text, Image } from 'react-native'
import "./assets/css/main.css"
import Axios from 'axios'
import success from './images/success.png';


export default function LoginPage({ navigation }) {
	const navigate = useNavigate();
    const path = useLocation();

	const newsession = localStorage.getItem('newsession')
	const noteslist = localStorage.getItem('noteslist')
	localStorage.getItem('newsession', JSON.stringify(newsession))
	localStorage.getItem('noteslist', JSON.stringify(noteslist))

	const reg = {
		session: newsession,
		noteslist: noteslist,
	}

	const handleSubmit = () => {
		Axios.post("http://localhost:8081/notestable", reg)
		console.log(reg)
		navigate('/')
	}

  return (
	<View>
		<View>
			<View style={styles.middle}>
				<Text style={styles.title}>Успіх!</Text>
			</View>
			<img src={success} alt="success" style={{ width: '22%', marginLeft: '39%', height: '22%' }}/>;
			<View style={styles.middle}>
				<Text style={styles.title2}>Пароль змінено</Text>
			</View>
			<form action="" onSubmit={handleSubmit}>
				<View style={styles.spaceBtn}>
					<View style={styles.btn}>
						<View style={styles.middleBtn}>
							<View style={styles.middleBtn} onClick={handleSubmit}>
								<Text style={styles.textBtn}>Повернутися</Text>
							</View>
						</View>
					</View>
				</View>
			</form>
		</View>
	</View>
  	)
}

const styles = StyleSheet.create({
	middle: {
		alignItems: 'center',
	},
	title: {
		fontWeight: 600,
		fontSize: 40,
		marginTop: "6%",
		marginBottom: "1%",
	},
	title2: {
		fontSize: 30,
		marginBottom: "2%",
	},
	spaceBtn: {
		marginTop: 10,
		backgroundColor: '#f2f2f2',
		alignItems: 'center',
	},
	btn: {
		width: 260,
		height: 60,
		borderRadius: 35,
		backgroundColor: '#5c358f',
		alignItems: 'center',
	},
	middleBtn: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
		height: 60,
	},
	textBtn: {
		fontSize: 25,
		fontWeight: 500,
		color: 'white',
		paddingRight: 8,
		paddingLeft: 8,
		paddingBottom: 5,
	},
});
