import React, { useState, useEffect } from 'react'
import { StyleSheet, View, SafeAreaView, Text } from 'react-native'
import { useLocation, useNavigate } from 'react-router-dom'
import { nanoid } from 'nanoid'
import NotesList from './NotesList'
import Search from './Filter'
import './assets/css/main.css'


export default function Expenses({ navigation }) {
	const navigate = useNavigate();
  	const path = useLocation();

	const [notes, setNotes] = useState([
		{
			id: nanoid(),
			key: 'Зразок',
			text: 'Тут ключ',
			date: 'Місце для передавання ключа',
			today: '25/10/2023'
		},
	]);

	const [searchText, setSearchText] = useState('');

	useEffect(() => {
		const savedNotes = JSON.parse(
			localStorage.getItem('noteslist')
		);

		if (savedNotes) {
			setNotes(savedNotes);
		}
	}, []);

	useEffect(() => {
		localStorage.setItem(
			'noteslist',
			JSON.stringify(notes)
		);
	}, [notes]);

	const deleteNote = (id) => {
		const newNotes = notes.filter((note) => note.id !== id);
		setNotes(newNotes);
	};

  	return (
    <div id="page-wrapper">

    <SafeAreaView>
		<View>
			<Text style={styles.h1}>Шаблон «Мої витрати»</Text>
			<a onClick={() => navigate('/createpage')}>add</a>
			<Filter handleSearchNote={setSearchText} />
			<NotesList
				notes={notes.filter((note) =>
					note.text.toLowerCase().includes(searchText)
				)}
				handleDeleteNote={deleteNote}
			/>
		</View>
	</SafeAreaView>

		<div id="footer" style={{ paddingTop: "2%", backgroundColor: "#f2f2f2" }}>
			<div className="copyright">
				<ul className="menu">
					<li>&copy; Website on React Native. All rights reserved</li><li>Development and design by <a href="/">Tockyj Bohdan</a></li>
				</ul>
			</div>
        </div>
    </div>
  	)
}

const styles = StyleSheet.create({
	middle: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
	},
	btns: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
		width: '90%',
		marginLeft: '4%',
		marginTop: 20,
	},
	text: {
		fontSize: 20,
		marginTop: -1,
		marginLeft: 1,
	},
	txt: {
		fontSize: 16,
		marginTop: 12,
		marginBottom: 4,
		color: '#888'
	},
	txtdef: {
		fontSize: 16,
		color: '#888'
	},
	middletxt: {
		alignItems: 'center',
	},
	h1: {
		textAlign: 'center',
		marginTop: '3%',
		marginBottom: '2%',
		fontSize: '27px',
		fontWeight: 'bold'
	}
});
