import React, { useState, useEffect } from 'react'
import { json, useLocation, useNavigate } from "react-router-dom"
import Validation from './ChangeValidation'
import { nanoid } from 'nanoid'
import Navbar from "./Navbar"
import NavbarAccount from "./NavbarAccount"
import "./assets/css/main.css"
import Axios from 'axios'


export default function Account({ navigation }) {
	const session = localStorage.getItem('session')

	const navigate = useNavigate();
	const path = useLocation();

	let ses = session;
	let nav = '';

	if (ses === null) {
		nav = <Navbar />
	} else {
		nav = <NavbarAccount />
	}

	const [errors, setErrors] = useState({})

	const handleInput = (event) => {
		setValues(prev => ({...prev, [event.target.name]: [event.target.value]}))
	}

	const [email, setEmail] = useState([
		{
			email: '',
			password: ''
		},
	]);

	const [values, setValues] = useState({
		password: '',
		email: '',
	})

	useEffect(() => {
		const savedNotes = JSON.parse(
			localStorage.getItem('session')
		);

		if (savedNotes) {
			setEmail(savedNotes);
		}
	}, []);

	localStorage.setItem('email', JSON.stringify(session))

	delete email['password']
	localStorage.setItem('email', JSON.stringify(email))

	const Exit = () => {
		localStorage.clear()
		navigation.navigate('StartPage')
	}

	const handleSubmit = (event) => {
		event.preventDefault();
		setErrors(Validation(values));
		if(errors.email === "" && errors.password === "") {
			Axios.put("http://localhost:19007/login", values)
			navigation.navigate('ChangeComplete')
		}
	}

  	return (
    <div id="page-wrapper">
    {nav}

    <div id="footer" style={{ background: "white" }}>
		<div className="container" style={{ background: "white", marginBottom: "-5%" }}>
			<div className="row">
				<section className="6u 12u(narrower) 12u$(mobilep)">
					<h3 style={{ marginLeft: "10%" }}>Змінити пароль</h3>
					<form style={{ width: "80%", marginLeft: "10%" }}>
						<div className="row 50%">
							<div className="12u">
								<input type="email" name="email" id="email" placeholder="Ваша електронна пошта*"/>
							</div>
						</div>
						<div className="row 50%">
							<div className="12u">
								<input type="email" name="email" id="email" placeholder="Придумайте новий пароль*"/>
							</div>
						</div>
						<div className="row 50%">
							<div className="12u">
								<ul className="actions" style={{ marginBottom: "20px" }}>
									<li><input type="submit" className="button alt" value="Змінити пароль"/></li>
								</ul>
							</div>
						</div>
					</form>
				</section>

				<section className="6u 12u(narrower) 12u$(mobilep)">
					<h3 style={{ marginLeft: "10%" }}>Ваша пошта</h3>
					<div className="row 50%" style={{ width: "83.4%", marginLeft: "6.7%" }}>
						<div className="12u">
							<ul className="actions" style={{ marginBottom: "20px" }}>
								<li><input type="submit" className="button alt" value="Вийти з акаунту"/></li>
							</ul>
						</div>
					</div>
				</section>
			</div>
		</div>
	<br></br>
	<br></br>
	<br></br>
	<br></br>
		<br></br>
			<div className="copyright">
				<ul className="menu">
					<li>&copy; JavaScript. All rights reserved</li><li>Design: <a href="http://">Tockyj Bohdan</a></li>
				</ul>
        	</div>
		</div>
    </div>
  	)
}
