import React, { useState, useEffect } from 'react'
import { StyleSheet, SafeAreaView, View, Text } from 'react-native'
import { nanoid } from 'nanoid'
import AddNote from './AddNote'


export default function CreatePage({ navigation }) {

	const [notes, setNotes] = useState([
		{
			id: nanoid(),
			key: 'Test',
			text: 'Test',
			date: 'Test',
			today: '26/04/2022',
		},
	]);

	const [searchText] = useState('');

	useEffect(() => {
		const savedNotes = JSON.parse(
			localStorage.getItem('noteslist')
		);

		if (savedNotes) {
			setNotes(savedNotes);
		}
	}, []);

	useEffect(() => {
		localStorage.setItem(
			'noteslist',
			JSON.stringify(notes)
		);
	}, [notes]);

	const addNote = (key, text) => {
		const date = new Date();
		const today = new Date();
		const newNote = {
			id: nanoid(),
			key: key,
			text: text,
			date: date.toLocaleDateString(),
			today: today.toLocaleDateString(),
		};
		const newNotes = [...notes, newNote];
		setNotes(newNotes);
	};

	const deleteNote = (id) => {
		const newNotes = notes.filter((note) => note.id !== id);
		setNotes(newNotes);
	};

  	return (
    <div id="page-wrapper">

    <SafeAreaView>
			<View>
				<AddNote
					notes={notes.filter((note) =>
						note.text.toLowerCase().includes(searchText)
					)}
					handleAddNote={addNote}
					handleDeleteNote={deleteNote}
				/>
			</View>
			<View style={styles.spaceBtn}>

				<View style={styles.middle}>
					<Text style={styles.title}>
						<b>після збереження, новий запис {'\n'}  потрапить до ваших записів!</b>
					</Text>
				</View>
			</View>
		</SafeAreaView>

		<div id="footer" style={{ paddingTop: "2%" }}>
			<div className="copyright">
				<ul className="menu">
					<li>&copy; Website on React Native. All rights reserved</li><li>Development and design by <a href="/">Tockyj Bohdan</a></li>
				</ul>
			</div>
        </div>
    </div>
  	)
}

const styles = StyleSheet.create({
	spaceBtn: {
		marginTop: 15,
		marginBottom: 15,
		backgroundColor: '#f2f2f2',
		alignItems: 'center',
	},
	btn: {
		width: 260,
		borderRadius: 30,
		backgroundColor: '#5c358f',
		alignItems: 'center',
	},
	middle: {
		alignItems: 'center',
	},
	title: {
		fontSize: 18,
		marginTop: 10,
		marginBottom: 20,
	},
	txt: {
		fontSize: 16,
		marginTop: 12,
		marginBottom: 4,
		color: '#888'
	},
	txtdef: {
		fontSize: 16,
		color: '#888'
	},
	middleBtn: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
		height: 60,
	},
	textBtn: {
		fontSize: 25,
		fontWeight: 500,
		color: 'white',
		paddingRight: 8,
		paddingLeft: 8,
		paddingBottom: 5,
	},
});
