import React, { useState } from 'react'
import { useLocation, useNavigate } from "react-router-dom"
import "./assets/css/main.css"
import { MdSettings } from 'react-icons/md'
import Axios from 'axios'


const NavbarAccount = () => {
    const navigate = useNavigate();
    const path = useLocation();

	const session = localStorage.getItem('session')

	const [data, setData] = useState ([])

	data.map((notes) => {
		console.log(notes.noteslist)
		localStorage.setItem('noteslist', (notes.noteslist))
	})

	const handleSubmit = () => {
	Axios.get('http://localhost:8081/MainPage', { params: { session: session } })
	.then(res => { setData(res.data) 
	function s1() {
	navigate('/MainPage') }

	setTimeout(s1, 400)
	})
	}

    return (
    <div id="page-wrapper">
		<div id="header">
			<h1><a href="index.html" class="logo" id="logo">ItCreative <em>— твій інструмент для апгрейду креативності</em></a></h1>
				<nav id="nav" style={{ display: "flex" }}>
					<nav id="nav" style={{ width: "82.5%" }}>
						<ul style={{ width: "83%", textAlign: "left", justifyContent: "flex-start", marginLeft: "-0.7em" }}>
							<li><a href="/">Головна</a></li>
							<li><a href="/news">Новини</a></li>
							<li><a href="/exercises">Вправи</a></li>
							<li><a href="2_chapter.html">Додати запис</a></li>
							<li><a onClick={(handleSubmit)}>Мої записи</a></li>
						</ul>

						<ul style={{ height: "100%", marginTop: "1.3%", marginBottom: "1.2%" }}>
							<li style={{ textAlign: "right", justifyContent: "flex-end" }}>
								<li>
									<div className="menu_button_signup" style={{ display: "flex", alignItems: "center" }}>
									<a onClick={() => navigate("/signup")} style={{ color: "white" }}>Мій акаунт</a>
									<MdSettings color='#9e5cf6'
									onClick={() => navigate("/signup")}
									size='1.2em'
									/>
									</div>
								</li>
							</li>
						</ul>
					</nav>
				</nav>
      		</div>
        </div>
    );
};

export default NavbarAccount
