import React, { useState, useEffect } from 'react'
import { StyleSheet, View, SafeAreaView } from 'react-native'
import { nanoid } from 'nanoid'
import NotesList from './NotesList'
import Search from './Search'
import Navbar from './Navbar'
import NavbarAccount from './NavbarAccount'
import './assets/css/main.css'
import Axios from 'axios'


export default function MainPage({ navigation }) {
	const session = localStorage.getItem('session')

	let ses = session;
	let nav = '';

	if (ses === null) {
		nav = <Navbar />
	} else {
		nav = <NavbarAccount />
	}

	const [notes, setNotes] = useState([
		{
			id: nanoid(),
			key: 'Зразок',
			text: 'Тут ключ',
			date: 'Місце для передавання ключа',
			today: '25/10/2023'
		},
	]);

	const [searchText, setSearchText] = useState('');

	useEffect(() => {
		const savedNotes = JSON.parse(
			localStorage.getItem('noteslist')
		);

		if (savedNotes) {
			setNotes(savedNotes);
		}
	}, []);

	useEffect(() => {
		localStorage.setItem(
			'noteslist',
			JSON.stringify(notes)
		);
	}, [notes]);

	const deleteNote = (id) => {
		const newNotes = notes.filter((note) => note.id !== id);
		setNotes(newNotes);

		function s1() {
			const session = localStorage.getItem('session')
			const noteslist = localStorage.getItem('noteslist')

			const values = {
			noteslist: noteslist,
			session: session,
			}

			console.log(values)
			Axios.put("http://localhost:8081/notes", values)
			}
			setTimeout(s1, 400)
	};

  	return (
    <div id="page-wrapper">
    {nav}

    <SafeAreaView>
		<View>
			<Search handleSearchNote={setSearchText} />
			<NotesList
				notes={notes.filter((note) =>
					note.text.toLowerCase().includes(searchText)
				)}
				handleDeleteNote={deleteNote}
			/>
		</View>
	</SafeAreaView>

		<div id="footer" style={{ paddingTop: "2%", backgroundColor: "#f2f2f2" }}>
			<div className="copyright">
				<ul className="menu">
					<li>&copy; Website on React Native. All rights reserved</li><li>Development and design by <a href="/">Tockyj Bohdan</a></li>
				</ul>
			</div>
        </div>
    </div>
  	)
}

const styles = StyleSheet.create({
	middle: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
	},
	btns: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
		width: '90%',
		marginLeft: '4%',
		marginTop: 20,
	},
	text: {
		fontSize: 20,
		marginTop: -1,
		marginLeft: 1,
	},
	txt: {
		fontSize: 16,
		marginTop: 12,
		marginBottom: 4,
		color: '#888'
	},
	txtdef: {
		fontSize: 16,
		color: '#888'
	},
	middletxt: {
		alignItems: 'center',
	},
});
