import React, { useState, useEffect } from 'react'
import { StyleSheet, SafeAreaView, View, Text } from 'react-native'
import { nanoid } from 'nanoid'
import AddNote from './AddNote'
import ExpenseItem from './ExpenseItem'
import Filter from './Filter'


export default function CreatePage({ navigation }) {

	const [notes, setNotes] = useState([
		{
			id: nanoid(),
			key: 'Test',
			text: 'Test',
			date: 'Test',
			today: '26/04/2022',
		},
	]);

	const [searchText] = useState('');

	useEffect(() => {
		const savedNotes = JSON.parse(
			localStorage.getItem('noteslist')
		);

		if (savedNotes) {
			setNotes(savedNotes);
		}
	}, []);

	useEffect(() => {
		localStorage.setItem(
			'noteslist',
			JSON.stringify(notes)
		);
	}, [notes]);

	const addNote = (key, text) => {
		const date = new Date();
		const today = new Date();
		const newNote = {
			id: nanoid(),
			key: key,
			text: text,
			date: date.toLocaleDateString(),
			today: today.toLocaleDateString(),
		};
		const newNotes = [...notes, newNote];
		setNotes(newNotes);
	};

	const deleteNote = (id) => {
		const newNotes = notes.filter((note) => note.id !== id);
		setNotes(newNotes);
	};

  	return (
    <SafeAreaView>
			<Text style={styles.h1}>Шаблон «Мої витрати»</Text>
			<View style={styles.block}>
				<AddNote
					notes={notes.filter((note) =>
						note.text.toLowerCase().includes(searchText)
					)}
					handleAddNote={addNote}
					handleDeleteNote={deleteNote}
				/>
			</View>
			<Filter handleSearchNote={setSearchText} />
			<ExpenseItem
				notes={notes.filter((note) =>
					note.text.toLowerCase().includes(searchText)
				)}
				handleDeleteNote={deleteNote}
			/>
		</SafeAreaView>
  	)
}

const styles = StyleSheet.create({
	btn: {
		width: 260,
		borderRadius: 30,
		backgroundColor: '#5c358f',
		alignItems: 'center',
	},
	middle: {
		alignItems: 'center',
	},
	title: {
		fontSize: 18,
		marginTop: 10,
		marginBottom: 20,
	},
	txt: {
		fontSize: 16,
		marginTop: 12,
		marginBottom: 4,
		color: '#888'
	},
	txtdef: {
		fontSize: 16,
		color: '#888'
	},
	middleBtn: {
		display: 'flex',
		flexDirection: 'row',
		alignItems: 'center',
		justifyContent: 'space-between',
		height: 60,
	},
	textBtn: {
		fontSize: 25,
		fontWeight: 500,
		color: 'white',
		paddingRight: 8,
		paddingLeft: 8,
		paddingBottom: 5,
	},
	h1: {
		textAlign: 'center',
		marginTop: '3%',
		marginBottom: '2%',
		fontSize: '27px',
		fontWeight: 'bold'
	},
});
